import { ethers, web3 } from "hardhat";

const EXISTING_PROOF_HEX =
    "";

const MERKLE_PROOF = [
    "0x4266e5ed962b6ce485671a9785ab7fcb8f7b46dd164212d73eb1ead6fd9eafd8",
    "0xe81f29031ebf36324242e4cddb92d7358ca44bf8ee6e389f3308f1212a1eede1",
    "0xc951386acc7ce3b119e683315c7e710a7d04d34aa7e37067326100c8b3386891",
];

const FlightSurety = artifacts.require("FlightSurety")


async function main() {
    const [deployer] = await ethers.getSigners();
    console.log("STARTING FLIGHT SURETY TEST");

    const insurance = await FlightSurety.new(deployer.address)
//    const insurance = await FlightSurety.deploy(deployer.address) as FlightSuretyInstance;
  //  await insurance.waitForDeployment();
    console.log(await insurance.address)
    console.log(insurance)
    const insuranceAddr = await insurance.address;
    //const insContract = await FlightSuretyInstance.at(insuranceAddr)
    console.log(`FlightSurety Deployed: ${insuranceAddr}`);

    // ðŸ” DEBUG: PRINT AVAILABLE FUNCTIONS
    // This tells us exactly what Hardhat sees in the contract
    console.log("\nCONTRACT FUNCTIONS FOUND:");
//    insurance.interface.forEachFunction((f) => {
//        console.log(`   - ${f.name}`);
//    });
    console.log("--------------------------------\n");

    // 2. FUND
    const fundTx = await deployer.sendTransaction({
        to: insuranceAddr,
        value: ethers.parseUnits("1.0", "ether"),
    });
    await fundTx.wait();

    // 3. DECODE & CLAIM
    console.log("Decoding proof...");
    const IWeb2JsonVerification = await artifacts.require("IWeb2JsonVerification");
    const responseType = IWeb2JsonVerification._json.abi[0].inputs[0].components[1];
    const decodedDataStruct = web3.eth.abi.decodeParameter(responseType, EXISTING_PROOF_HEX);

    console.log("Calling claimPayout...");

    const tx = await insurance.claimPayout({
        merkleProof: MERKLE_PROOF,
        data: decodedDataStruct,
    });

    
    console.log("SUCCESS! Payout claimed.");
}

main().catch(console.error);
